  `brushName` varchar(255) DEFAULT NULL COMMENT '刷单人员名称',
  `brushAccount` varchar(255) DEFAULT NULL COMMENT '刷单账号',
  `burshSuccessImg` varchar(255) DEFAULT NULL COMMENT '刷单成功截图路径',
