DROP TABLE IF EXISTS `tbl_commodity`;
CREATE TABLE `tbl_commodity` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键id',
  `esname` varchar(50) DEFAULT NULL COMMENT '货品英文',
  `cnname` varchar(50) DEFAULT NULL COMMENT '货品中文',
  `categories_id` varchar(255) DEFAULT NULL COMMENT '货品分类',
  `sku` varchar(255) DEFAULT NULL COMMENT '货品sku',
  `spu` varchar(255) DEFAULT NULL COMMENT '货品spu',
  `stock` varchar(255) DEFAULT NULL COMMENT '库存',
  `title` varchar(255) DEFAULT NULL COMMENT '标题',
  `discount_price` decimal(10,2) DEFAULT NULL COMMENT '折扣价格',
  `origin_price` decimal(10,2) DEFAULT NULL COMMENT '原价格',
  `color` varchar(50) DEFAULT NULL COMMENT '颜色',
  `product_size` varchar(255) DEFAULT NULL COMMENT '产品尺寸',
  `weight` double(10,0) DEFAULT NULL COMMENT '重量',
  `package_size` varchar(255) DEFAULT NULL COMMENT '包裹尺寸',
  `brands` varchar(255) DEFAULT NULL COMMENT '品牌',
  `desc` varchar(255) DEFAULT NULL COMMENT '描述',
  `supplier_id` varchar(255) DEFAULT NULL COMMENT '供应商',
  `purchase_price` decimal(10,2) DEFAULT NULL COMMENT '采购价',
  `picture_id` varchar(255) DEFAULT NULL COMMENT '产品图片id',
  `createtime` datetime DEFAULT NULL ON UPDATE CURRENT_TIMESTAMP COMMENT '商品添加时间',
  `updatetime` datetime DEFAULT NULL ON UPDATE CURRENT_TIMESTAMP COMMENT '商品更新时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8 COMMENT='货品管理表';

