  `way` varchar(255) DEFAULT NULL COMMENT '物流方式',
  `unit` varchar(255) DEFAULT NULL COMMENT '物流承接单位',
  `customer_phone` varchar(255) DEFAULT NULL COMMENT '客服电话',
  `tracking_url` varchar(255) DEFAULT NULL COMMENT '订单追踪网址',
  `position` varchar(255) DEFAULT NULL COMMENT '物流当前位置',
