  `transaction_date` varchar(50) DEFAULT NULL COMMENT '订单时间',
  `country` varchar(255) DEFAULT NULL COMMENT '国家',
  `province` varchar(255) DEFAULT NULL COMMENT '州 | 省',
  `city` varchar(255) DEFAULT NULL COMMENT '城市',
  `county` varchar(255) DEFAULT NULL COMMENT '县 | 区 | 市',
  `detail_address` varchar(255) DEFAULT NULL COMMENT '详细地址',
